def obtenerMultiplos(numero):
    multiplos = []
    for i in range(1,11):
       multiplos.append(numero * i)
    return multiplos

def obtenerDivisores(numero):
    lista = []
    for divisor in range(2, numero):
       if numero % divisor == 0:
            lista.append(divisor)
    return lista

def obtenerNesimoFibonacci(position):
    fib1, fib2 = 0,1
    if position == 1:
       return fib1
    if position == 2:
       return fib2
    for i in range(0, position-2):
       fib1, fib2 = fib2, fib1 + fib2
    return fib2

funciones = [obtenerMultiplos, obtenerDivisores, obtenerNesimoFibonacci]
print("3) ",funciones)
print("3) multiplos del 7=>", funciones[0](7))
print("3) divisores del 100 =>", funciones[1](100))
print("3) 9no fibonacci =>", funciones[2](9))

