ventas = ["A032-52Unidades", "B001-29Unidades", "A125-15Unidades", "A032-22Unidades", "P009-25Unidades", "B005-20Unidades", "B001-19Unidades", "P009-31Unidades", "B005-22Unidades", "W307-15Unidades", "A011-31Unidades", "P019-18Unidades", "A011-20Unidades", "R001-20Unidades", "P019-19Unidades", "A001-12Unidades", "A125-20Unidades", "R001-31Unidades", "Z052-52Unidades", "W307-31Unidades", "Z025-42Unidades", "Z052-10Unidades", "Z278-30Unidades", "Z025-24Unidades", "Z278-21Unidades", "A001-32unidades"]
def calcularVentas(ventas):
   precio = {"A001": 31000,"A011": 25000,"A032": 43000,"A125": 55000,"B001": 10000,"B005": 20000,"P009": 30000,"P019": 49000,"R001": 60000,"W307": 90000,"Z052": 35000,"Z025": 27000,"Z278": 65000}
   dinero_recaudado = 0
   for venta in ventas:
        cod = venta[0:4]
        cant = int(venta[5:7])
        dinero_recaudado += cant * precio[cod]
   return dinero_recaudado


print("4) calcular ventas => ", calcularVentas(ventas))

