unidades = [1,1,1,1,1,1,1]  #variable de apoyo

def calcularSalario(nombre, unidades):
   comisiones = [50000 * 0.05,70000 * 0.04,  40000 * 0.06,25000 * 0.07,35000 * 0.05,80000 * 0.03,95000 * 0.02]
   salario = {"nombre":nombre, "salario": 1200000}
   for i in range(7):
        salario["salario"] += unidades[i] * comisiones[i]
   return salario

print("5) calcular salario [1,1,1,1,1,1,1]=>", calcularSalario("Cristian Pachon", unidades))


